import React, { useState } from "react";
import "./App.css";

function App() {
  const [tareas, setTareas] = useState([]);
  const [inputTarea, setInputTarea] = useState("");

  const guardarTareas = () => {
    const tareasArray = tareas.map((tarea) => tarea.texto);
    localStorage.setItem("tareas", JSON.stringify(tareasArray));
  };

  const cargarTareas = () => {
    const tareasArray = JSON.parse(localStorage.getItem("tareas")) || [];
    const tareasObjeto = tareasArray.map((tarea, index) => ({
      id: index,
      texto: tarea,
    }));
    setTareas(tareasObjeto);
  };

  const agregarTarea = () => {
    if (inputTarea) {
      const nuevaTarea = { id: tareas.length, texto: inputTarea };
      setTareas([...tareas, nuevaTarea]);
      setInputTarea("");
      guardarTareas();
    } else {
      alert("Por favor ingresa una tarea");
    }
  };

  const eliminarTarea = (id) => {
    try {
      const indice = tareas.findIndex((tarea) => tarea.id === id);
      const tareasActualizadas = [...tareas];
      tareasActualizadas.splice(indice, 1);
      setTareas(tareasActualizadas);
      guardarTareas();
    } catch (error) {
      alert("Por favor selecciona una tarea");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <ul>
          {tareas.map((tarea, index) => (
           <li key={tarea.id}>
              <button onClick={() => eliminarTarea(tarea.id)}>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  className="feather feather-check-circle"
                >
                  <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                  <polyline points="22 4 12 14.01 9 11.01"></polyline>
                </svg>
              </button>
              {index + 1}. {tarea.texto}
            </li>
          ))}
        </ul>
        <input
          type="text"
          value={inputTarea}
          onChange={(e) => setInputTarea(e.target.value)}
        />
        <button onClick={agregarTarea}>Agregar tarea</button>
        <button onClick={cargarTareas}>Cargar tareas</button>
      </header>
    </div>
  );
}

export default App;
