import React, { useState } from "react";
import "./App.css";

function App() {
  const [tareas, setTareas] = useState([]);
  const [inputTarea, setInputTarea] = useState("");

  const guardarTareas = () => {
    const tareasArray = tareas.map((tarea) => tarea.texto);
    localStorage.setItem("tareas", JSON.stringify(tareasArray));
  };

  const cargarTareas = () => {
    const tareasArray = JSON.parse(localStorage.getItem("tareas")) || [];
    const tareasObjeto = tareasArray.map((tarea, index) => ({
      id: index,
      texto: tarea,
    }));
    setTareas(tareasObjeto);
  };

  const agregarTarea = () => {
    if (inputTarea) {
      const nuevaTarea = { id: tareas.length, texto: inputTarea };
      setTareas([...tareas, nuevaTarea]);
      setInputTarea("");
      guardarTareas();
    } else {
      alert("Por favor ingresa una tarea");
    }
  };

  const eliminarTarea = (id) => {
    try {
      const indice = tareas.findIndex((tarea) => tarea.id === id);
      const tareasActualizadas = [...tareas];
      tareasActualizadas.splice(indice, 1);
      setTareas(tareasActualizadas);
      guardarTareas();
    } catch (error) {
      alert("Por favor selecciona una tarea");
    }
  };

  return (
    <div className="App">
      <header className="App-header right-align">
        <ul>
          {tareas.map((tarea, index) => (
            <li key={tarea.id} className="task-item">
              <button onClick={() => eliminarTarea(tarea.id)}>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  className="feather feather-trash"
                >
                  <polyline points="3 6 5 6 20 6 20 14 5 14 5 6"></polyline>
                  <path d="M16 10a4 4 0 0 1-8 0 4 4 0 0 1 8 0z"></path>
                </svg>
              </button>
              {index + 1}. {tarea.texto}
            </li>
          ))}
        </ul>
        <div className="input-container">
          <input
            type="text"
            value={inputTarea}
            onChange={(e) => setInputTarea(e.target.value)}
            placeholder="AÃ±ade una tarea..."
          />
          <button onClick={agregarTarea} className="large-button">
            Agregar tarea
          </button>
        </div>
        <button onClick={cargarTareas} className="large-button">
          Cargar tareas
        </button>
      </header>
    </div>
  );
}

export default App;
